def rangeSum(nums, n, left, right):
    sum_index = 0
    left = left-1
    right = right
    new_list = []

    while sum_index != n:
        prev_sum = 0

        for i in range(sum_index, n):
            cur_sum = nums[i] + prev_sum
            new_list.append(cur_sum)
            prev_sum = cur_sum

        sum_index += 1

    new_list.sort()
    mod = 10**9 + 7
    return sum(new_list[left:right]) % mod

